import styles from "./Items.module.scss";
import { Item } from "./item";
import { useAppSelector } from "../../hooks";
import { Loader } from "cutie-ui";
import { Game } from "../../redux/types/types";

interface ItemsProps {
  currentGames: Game[];
}

