const FIVE_MINUTES = 5 * 60 * 1000;

export function isCardAvailable(gameId: string) {
  const storedTime = localStorage.getItem(`game_${gameId}`);
  if (storedTime) {
    const currentTime = new Date().getTime();
    return currentTime - parseInt(storedTime) <= FIVE_MINUTES;
  }

  return false;
}
