import { Game } from "../redux/types/types";

const currentTime: number = new Date().toLocaleDateString;

export function addCardToLS(data?: Game) {
  localStorage.removeItem(`game_${data?.id}`);
  console.log();
  if (data) {
    localStorage.setItem(
      `game_${data.id}`,
      JSON.stringify({ data, currentTime })
    );
    console.log(
      // @ts-ignore
      JSON.parse(localStorage.getItem(`game_${data.id}`)).currentTime
    );
  }
}
