const FIVE_MINUTES: number = 0.1 * 60 * 1000;

export function isCardAvailable(gameId: string) {
  const localStorageData: string | null = localStorage.getItem(
    `game_${gameId}`
  );
  const storedTime = localStorageData
    ? JSON.parse(localStorageData).currentTime
    : null;

  if (storedTime) {
    const currentTime: number = new Date().getTime();
    if (currentTime - parseInt(storedTime) <= FIVE_MINUTES) {
      return true;
    } else {
      localStorage.removeItem(`game_${gameId}`);
      return false;
    }
  }

  return false;
}
